def floor_calc():
    floor = 0

    for i in apartment:
        if i == "(":
            floor = floor + 1
        if i == ")":
            floor = floor - 1

    return floor


def basement():
    floor = 0
    loop_number = 0

    for i in apartment:
        if floor == -1:
            return loop_number

        if i == "(":
            floor = floor + 1
            loop_number = loop_number + 1

        if i == ")":
            floor = floor - 1
            loop_number = loop_number + 1


